import { validationMixin } from "vuelidate";
import { required, maxLength, email } from "vuelidate/lib/validators";
export default {
  mixins: [validationMixin],
  validations: {
    name: {
      required: required,
      maxLength: maxLength(10)
    },
    email: {
      required: required,
      email: email
    },
    select: {
      required: required
    },
    checkbox: {
      checked: function checked(val) {
        return val;
      }
    }
  }
};
